INSERT INTO `m_pocket_goods_preview` VALUES (8, 7043, '韩束墨菊深度补水八件套组', '', 0.00, 0.00, '', '<div style="min-width: 320px; padding-bottom: 20px; line-height: 24px; margin: 0px auto; font-family: ''微软雅黑''; max-width: 750px; background: #f7eaf3; color: #555; font-size: 1em"><img alt="韩束墨菊深度补水八件套组 " src="http://www.wm18.com/product_img/V140587/1.jpg" />\r\n<p style="margin: 20px auto; width: 75%"><strong>墨菊深度补水八件套 补得进 储得足 锁得住（ICON）</strong><br />\r\n墨菊深度保湿氨基酸洁面乳 80ml<br />\r\n墨菊深度补水露 120ml<br />\r\n墨菊深度补水乳 60ml<br />\r\n墨菊深度补水精华素 30ml<br />\r\n墨菊深度补水霜 6g*3<br />\r\n&nbsp;</p>\r\n<img alt="韩束墨菊深度补水八件套组 " src="http://www.wm18.com/product_img/V140587/2.jpg" /><img alt="韩束墨菊深度补水八件套组 " src="http://www.wm18.com/product_img/V140587/3.jpg" />\r\n<p style="margin: 20px auto; width: 75%">补得进<br />\r\n瞬间渗入角质层，均匀分布，改善细胞间质构成，源源补充外在水润养分；<br />\r\n储得足<br />\r\n形成密集网状储水结构，有力阻隔内在水分流失，肌肤自然饱满水嫩；<br />\r\n锁得住<br />\r\n构建坚韧健康屏障牢牢锁住水润养分全力抗击干燥侵袭。</p>\r\n<img alt="韩束墨菊深度补水八件套组 " src="http://www.wm18.com/product_img/V140587/4.jpg" /><img alt="韩束墨菊深度补水八件套组 " src="http://www.wm18.com/product_img/V140587/5.jpg" /><img alt="韩束墨菊深度补水八件套组 " src="http://www.wm18.com/product_img/V140587/6.jpg" /><img alt="韩束墨菊深度补水八件套组 " src="http://www.wm18.com/product_img/V140587/7.jpg" /><img alt="韩束墨菊深度补水八件套组 " src="http://www.wm18.com/product_img/V140587/8.jpg" /><img alt="韩束墨菊深度补水八件套组 " src="http://www.wm18.com/product_img/V140587/9.jpg" /><img alt="韩束墨菊深度补水八件套组 " src="http://www.wm18.com/product_img/V140587/10.jpg" /><img alt="韩束墨菊深度补水八件套组 " src="http://www.wm18.com/product_img/V140587/11.jpg" /><img alt="韩束墨菊深度补水八件套组 " src="http://www.wm18.com/product_img/V140587/12.jpg" /><img alt="韩束墨菊深度补水八件套组 " src="http://www.wm18.com/product_img/V140587/13.jpg" /><img alt="韩束墨菊深度补水八件套组 " src="http://www.wm18.com/product_img/V140587/14.jpg" /><img alt="韩束墨菊深度补水八件套组 " src="http://www.wm18.com/product_img/V140587/15.jpg" /><img alt="韩束墨菊深度补水八件套组 " src="http://www.wm18.com/product_img/V140587/16.jpg" /><img alt="韩束墨菊深度补水八件套组 " s